import { useState, useEffect } from 'react';

//Components import
import Moves from './Moves';
import PokemonListFight from './PokemonListFight';
import ResultPage from './ResultPage';
import Mewtwo from './Mewtwo';
import CalcHpAndAtt from './CalcHpAndAttack';

// Images import
import grassImage from '../images/grass.png';

// CSS import
import './css/fight.css';

const Fight = () => {
    const [pokemonList, setPokemonList] = useState(JSON.parse(localStorage.getItem('trainer-pokemon-list')));
    const [currentPokemon, setCurrentPokemon] = useState(pokemonList[0]);

    useEffect(() => {
        setCurrentPokemon(pokemonList);
    }, [pokemonList]);

    return(
        <section className='fightcontent'>
            <CalcHpAndAtt pokemonlist={pokemonList} setpokemonlist={value => setPokemonList(value)}/>
            <div className="content-fight">
                <Mewtwo />
                <div className="content-fight-bottom">
                    <img src={grassImage} alt="Grass image"/>
                    <div className="content-fight-bottom-pokemon">
                        <img src={currentPokemon.imgSpriteBack} alt="current pokemon"/>
                    </div>

                    <section>
                        <div className="content-fight-bottom-info">
                            <div className="content-fight-bottom-info-namelvl">
                                <p className="pokemon-name">{currentPokemon.name}</p>
                                <p className="pokemon-lvl">lvl. {currentPokemon.lvl}</p>
                            </div>
                            <div className="content-fight-bottom-info-hp">
                                <p className="hp-text">HP</p>
                                <div>
                                    <p className="hp-value">{currentPokemon.currentHP}</p>
                                    <p className="hp-value"> / </p>
                                    <p className="hp-value current-starting-hp">{currentPokemon.HP}</p>
                                </div>
                            </div>
                        </div>
                    </section>
                </div>
            </div>
            <Moves />
            <PokemonListFight />
            <ResultPage />
        </section>

    )
}

export default Fight;
