const calculateHP = () => {
    let hp = 0;
    pokemonList.map(pokemon => {
        let bhs = pokemon.hpBS;
        let lvl = pokemon.lvl;
        hp = (((2 * bhs + 100) * lvl) / 100) + 41;
        pokemon.HP = Math.ceil(hp);
        pokemon.currentHP = Math.ceil(hp);
    });
}

const ClacHpAndAttack = () => {
    calculateHP();
    return(
        <div/>
    )
}

export default ClacHpAndAttack;
